import React from "react";
import App from "next/app";
import Menu from "../components/menu";
import Container from "../components/Container";
import Footer from "../components/footer";
import AccessToken from "../components/AccessToken";
import Head from "next/head";
import Router from "next/router";
import Cookies from "js-cookie";
import { initializeStore } from "../store";
import { Provider } from "react-redux";
import { connect } from "react-redux";
import withReduxStore from "../with-redux-store";
import queryString from "query-string";

import "../styles/_app.scss";
import _ from "lodash";
import axios from "axios";
import config from "../config";

class CustomApp extends App {
    static async getInitialProps({ ctx }) {
        const res = ctx.res ? ctx.res : false;
        const spotifyAuth = res.spotifyAuth || config.spotifyAuth;
        const query = res.req ? res.req.query : {};
        const host = res.req ? res.req.headers.host : "";

        let authed = false;
        if (query.code && host) {
            try {
                const response = await axios.post(
                    `http://${host}/spotify/auth?${queryString.stringify(
                        query
                    )}`
                );
                console.log(response.data);
                if (response.status == 200 && response.data) {
                    authed = true;
                }
            } catch (e) {
                console.log(e);
            }
        }

        return { query, spotifyAuth, authed: authed };
    }

    constructor(props) {
        super(props);
        this.state = {
            enabled: false
        };
    }

    componentDidMount() {
        Router.replace(location.pathname);
        /* const { query, cookies, spotifyAuth } = this.props;
        let pass = true;
        if (!cookies.code && !query.code) {
            location.href = spotifyAuth;
            pass = false;
        } else if (query.code && !cookies.code) {
            Cookies.set("code", query.code);
            Router.replace(location.pathname);
        }
        this.setState({ enabled: pass }); */
    }

    render() {
        const { Component, pageProps, reduxStore } = this.props;
        const enabled = !!reduxStore.getState().Spotify.access_token;
        return (
            <Provider store={reduxStore}>
                <Head key="head">
                    <title>Musikit</title>
                </Head>
                {enabled ? (
                    <React.Fragment>
                        <div className="flex-container">
                            <Menu />
                            <Container>
                                <Component {...pageProps} />
                            </Container>
                        </div>
                        <Footer />
                    </React.Fragment>
                ) : (
                    <AccessToken />
                )}
            </Provider>
        );
    }
}

export default withReduxStore(CustomApp);
