import React from "react";
import App from "next/app";
import Menu from "../components/menu";
import Container from "../components/Container";
import Footer from "../components/footer";
import Head from "next/head";
import Router from "next/router";
import Cookies from "js-cookie";
import { initializeStore } from "../store";
import { Provider } from "react-redux";
import { connect } from "react-redux";
import withReduxStore from "../with-redux-store";
import parseCookies from "parse-cookies.js";

import "../styles/_app.scss";
import _ from "lodash";
import axios from "axios";
import config from "../config";

class CustomApp extends App {
    static async getInitialProps({ ctx }) {
        const res = ctx.res ? ctx.res : false;
        const spotifyAuth = res.spotifyAuth || config.spotifyAuth;
        const query = res.req ? res.req.query : {};

        let code = _.get(res, "req.cookies.code") ? res.req.cookies.code : "";
        let queryCode = _.get(res, "req.query.code") ? res.req.query.code : "";

        return { ...query, ...code };
    }

    componentDidMount() {
        const props = this.props;
        console.log(props);
        const urlParams = props;
        const code = urlParams.get("code");
        if (!Cookies.get("code") && !code) {
            Router.replace("/");
            return;
        }
        if (code) {
            Cookies.set("code", code);
            Router.replace(location.pathname);
        }
    }
    render() {
        const { Component, pageProps, reduxStore, router } = this.props;
        return (
            <Provider store={reduxStore}>
                <Head key="head">
                    <title>Musikit</title>
                </Head>
                <div className="flex-container">
                    <Menu />
                    <Container>
                        <Component {...pageProps} />
                    </Container>
                </div>
                <Footer />
            </Provider>
        );
    }
}

export default withReduxStore(CustomApp);
