import axios from "axios";
import config from "../config";
import Cookies from "js-cookie";

export const Auth = data => dispatch => {
    return dispatch({
        type: "Auth",
        value: data
    });
};

export const AccessToken = () => async dispatch => {
    const response = await axios.post(location.href + "spotify/auth");
    Cookies.remove("code");
    return dispatch({
        type: "AccessToken",
        value: response.data
    });
};

export const UserMe = data => async dispatch => {
    return dispatch({
        type: "UserMe",
        value: response
    });
};
